classprops.imageurl:
eval(
			begin
				HTTP.Referer:='http://cell.motivtelecom.ru/ekb/smssend';
				Stored['key'] := Html.RegExp('challenge\?k=(.*?)"',1);
				HTTP.HTTPMethod('GET','https://www.google.com/recaptcha/api/challenge?k='+ Stored['key'] + '&cachestop=0.' + RandomString('0123456789', 17));
				Stored['challenge']:=Html.RegExp('challenge : ''(.*?)''',1);
				HTTP.HTTPMethod('GET','https://www.google.com/recaptcha/api/reload?c=' + Stored['challenge'] + '&k=' + Stored['key'] + '&reason=i&type=image&lang=ru');
				Stored['challenge']:=Html.RegExp('finish_reload\(''(.*?)''',1);
				Result:='https://www.google.com/recaptcha/api/image?c='+Stored['challenge'];
			end;)

classprops.postdata:
eval(
			var
				DT: TDateTime;
			begin
					
				DT:=AdjustTimeZone(SMS['DeliveryTime'],360,false)-5*OneMinute;
				PostData.Add('smssend[name]','websms');
				PostData.Add('smssend[phone]',Copy(SMS['Phone'],2,10));
				PostData.Add('smssend[txt]',SMS['Text']);
				PostData.Add('smssend[send_date]',FormatDateTime('dd.mm.yyyy',DT));
				PostData.Add('smssend[hdelay]','0');
				PostData.Add('smssend[mdelay]','0');
				PostData.Add('recaptcha_challenge_field',Stored['challenge']);
				PostData.Add('recaptcha_response_field',SMS['Code']);
			end;)

classprops.success:
eval(begin
						Result:=HTML.ExistsStr('Сообщение отправлено') or HTML.ExistsStr('Сообщение будет отправлено');
						Stored['CheckUrl']:='http://cell.motivtelecom.ru/ekb/smssend/checkStatus?id=' + HTML.RegExp('checkStatus\?id=(.*?)<',1)+'&_=';
						end;)

classprops.error:
eval(
			begin
				Result:=HTML.RegExp('error_list">.*?<li>(.*?)</li>',1);
				if Result='' then
					Result:=HTML.RegExp('<li style="color: red;">(.*?)</li>',1);
			end;)

classprops.cd_success:
eval(
					begin
						if Html.ExistsStr('undelivered') then
							Result:='WAIT'
						else Result:=HTML.ExistsStr('delivered');		
					end;
					)
